mapper.select:

  select goods_swId, goods_swName, goods_swPrice, goods_swStock, goods_swDetail,
          goods_swRegDate, goods_swUpdateDate 
   from (
      select /* +INDEX_DESC(goods_sportswear idx_goods_sportswear) */ 
          rownum as rn, goods_swId, goods_swName, goods_swPrice, goods_swStock, goods_swDetail,
          goods_swRegDate, goods_swUpdateDate
    from goods_sportswear 
    where


        goods_swName like '%'||#{keyword}||'%' and


     rownum <= #{pageNum} * #{amount}
    )
where rn > (#{pageNum} - 1) * #{amount}
